import './App.scss';
import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPersonWalking } from '@fortawesome/free-solid-svg-icons';
import 'animate.css';

function App() {

  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const [isBright, setIsBright] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000);

    const handleKeyDown = (event) => {
      if (event.key === 'ArrowDown') {
        setIsBright(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(timer);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const timeFormatOptions = {
    hour: '2-digit',
    minute: '2-digit'
  };

  const dateFormatOptions = {
    day: 'numeric',
    month: 'long'
  };

  return (
    <div className="App">

      <section className={`systemPrompts ${isBright ? 'brighten' : 'dim'}`}>

      <p className={` ${isBright ? 'animate__animated animate__flash' : ''}`}><FontAwesomeIcon icon={faPersonWalking} /> <span>Motion Detected</span></p>

      </section>

      <section className={`userDetectionField ${isBright ? 'brighten' : 'dim'}`}>

      </section>

      <section className='dateTimeModule'>
        <h1>{currentDateTime.toLocaleTimeString([], timeFormatOptions)}</h1>
        <h2>{currentDateTime.toLocaleDateString([], dateFormatOptions)}</h2>
      </section>

    </div>
  );
}

export default App;
