import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000); // Update the date and time every minute

    return () => {
      clearInterval(timer); // Clear the interval when the component unmounts
    };
  }, []);

  // Format options for displaying the time without seconds
  const timeFormatOptions = {
    hour: '2-digit',
    minute: '2-digit'
  };

  return (
    <div className="App">
      <div className='userDetectionField'></div>
      <section className='dateTimeModule'>
        <h1>{currentDateTime.toLocaleTimeString([], timeFormatOptions)}</h1> {/* Display current time without seconds */}
        <h2>{currentDateTime.toLocaleDateString()}</h2> {/* Display current date */}
      </section>
    </div>
  );
}

export default App;
