import React from 'react';
import '../App.scss'; // Assume you have separate styling for this component

useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000);

    const handleKeyDown = (event) => {
      if (event.key === 'ArrowDown') {
        setIsBright(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(timer);
    };
  }, []);

function UserDetectionField({ isBright }) {
  return (
    <div className={`userDetectionField ${isBright ? 'brighten' : 'dim'}`}></div>
  );
}

export default UserDetectionField;
