import './App.scss';
import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPersonWalking } from '@fortawesome/free-solid-svg-icons';
import 'animate.css';
import axios from 'axios';
import QRCode from 'qrcode.react'; 

function App() {

  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const [isBright, setIsBright] = useState(false);
  const [ip, setIp] = useState('');
  const canvasRef = useRef(null);

  useEffect(() => {

    axios.get('http://localhost:3001/getIP') // Adjust the URL as needed
    .then(response => {
      setIp(response.data.ip);
      console.log(response.data.ip);
    })
    .catch(error => {
      console.error('There was an error fetching the IP address:', error);
    });


    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000);

    const handleKeyDown = (event) => {
      if (event.key === 'ArrowDown') {
        setIsBright(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(timer);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  useEffect(() => {
    if (canvasRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');
      const width = canvas.offsetWidth;
      const height = canvas.offsetHeight;
      canvas.width = width;
      canvas.height = height;

      const centerX = width / 2;
      const centerY = height / 2;
      const maxRadius = Math.sqrt(centerX * centerX + centerY * centerY);

      const dotCount = 50;
      const maxDotSize = 5;
      let angleOffset = 20; // Added for animation

      function draw() {
        ctx.fillStyle = 'black';
        ctx.fillRect(0, 0, width, height);

        for (let i = 0; i < dotCount; i++) {
          for (let j = 0; j < dotCount; j++) {
            const x = (width / dotCount) * i;
            const y = (height / dotCount) * j;

            const dx = centerX - x;
            const dy = centerY - y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            const size = maxDotSize * (distance / maxRadius);

            // Modify opacity based on an angle for animation
            const angle = Math.atan2(dy, dx) + angleOffset;
            const opacity = (Math.cos(angle) + 1) / 2;

            ctx.fillStyle = `rgba(100, 100, 100, ${opacity})`;

            ctx.beginPath();
            ctx.arc(x, y, size, 0, Math.PI * 2);
            ctx.closePath();
            ctx.fill();
          }
        }

        angleOffset += 0.01; // Update the angle for the next frame
        requestAnimationFrame(draw); // Recursively call draw
      }

      requestAnimationFrame(draw); // Start the animation
    }
  }, []);



  const timeFormatOptions = {
    hour: '2-digit',
    minute: '2-digit'
  };

  const dateFormatOptions = {
    day: 'numeric',
    month: 'long'
  };

  return (

    <>

    <div className="App">

      <section className={`systemPrompts ${isBright ? 'brighten' : ''}`}>

        <p className='animate__animated animate__flash'><FontAwesomeIcon icon={faPersonWalking}/> <span>Motion Detected</span></p>

      </section>

      <div className="dateTimeModule"> 

        <div className="qrCodeContainer">
          <QRCode value='https://www.youtube.com/watch?v=dQw4w9WgXcQ' size={100}  />
        </div>

        <div className="timeContainer">
          <h1>{currentDateTime.toLocaleTimeString([], timeFormatOptions)}</h1>
          <h2>{currentDateTime.toLocaleDateString([], dateFormatOptions)}</h2>
        </div>

    </div>

    </div>

    <section className={`userDetectionField ${isBright ? 'brighten' : ''}`}>
        <canvas ref={canvasRef}></canvas>
      </section>

    </>
  );
}

export default App;
