import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000); // Update the date and time every minute

    return () => {
      clearInterval(timer); // Clear the interval when the component unmounts
    };
  }, []);

  // Format options for displaying the time with AM/PM but without seconds
  const timeFormatOptions = {
    hour: '2-digit',
    minute: '2-digit',
    hour12: true
  };

  // Format options for displaying the date as number and month in text
  const dateFormatOptions = {
    day: 'numeric',
    month: 'long'
  };

  return (
    <div className="App">
      <div className='userDetectionField'></div>
      <section className='dateTimeModule'>
        <h1>{currentDateTime.toLocaleTimeString([], timeFormatOptions)}</h1> {/* Display current time with AM/PM */}
        <h2>{currentDateTime.toLocaleDateString([], dateFormatOptions)}</h2> {/* Display date as number and month in text */}
      </section>
    </div>
  );
}

export default App;
