import React from 'react';
import '../App.scss'; // Assume you have separate styling for this component

function DateTimeModule({ currentDateTime }) {
  const timeFormatOptions = {
    hour: '2-digit',
    minute: '2-digit'
  };

  const dateFormatOptions = {
    day: 'numeric',
    month: 'long'
  };

  return (
    <section className='dateTimeModule'>
      <h1>{currentDateTime.toLocaleTimeString([], timeFormatOptions)}</h1>
      <h2>{currentDateTime.toLocaleDateString([], dateFormatOptions)}</h2>
    </section>
  );
}

export default DateTimeModule;
