import React, { useState, useEffect } from 'react';
import '../App.scss'; // Assume you have separate styling for this component

function DateTimeModule() {

    const [currentDateTime, setCurrentDateTime] = useState(new Date());

  const timeFormatOptions = { hour: '2-digit', minute: '2-digit'};
  const dateFormatOptions = { day: 'numeric', month: 'long'};

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <section className='dateTimeModule'>
      <h1>{currentDateTime.toLocaleTimeString([], timeFormatOptions)}</h1>
      <h2>{currentDateTime.toLocaleDateString([], dateFormatOptions)}</h2>
    </section>
  );
}

export default DateTimeModule;
