import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 1000); // Update the date and time every second

    return () => {
      clearInterval(timer); // Clear the interval when the component unmounts
    };
  }, []);

  return (
    <div className="App">

      <div className='userDetectionField'></div>

      <section className='dateTimeModule'>
        <h1>{currentDateTime.toLocaleTimeString()}</h1> {/* Display current time */}
        <h2>{currentDateTime.toLocaleDateString()}</h2> {/* Display current date */}
      </section>

    </div>
  );
}

export default App;
