import './App.scss';
import React, { useState, useEffect } from 'react';
import UserDetectionField from './components/UserDetectionField';
import DateTimeModule from './components/DateTimeModule';

function App() {
  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const [isBright, setIsBright] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentDateTime(new Date());
    }, 60000);

    const handleKeyDown = (event) => {
      if (event.key === 'ArrowDown') {
        setIsBright(prev => !prev);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(timer);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
    <div className="App">
      <UserDetectionField isBright={isBright} />
      <DateTimeModule currentDateTime={currentDateTime} />
    </div>
  );
}

export default App;
